def count_handles(handles)
    puts "Il y a #{handles.size} handles dans cette array."
  end
  
  def shortest_handle(handles)
    shortest_handle = handles.min_by { |handle| handle.length }
    puts "Le handle le plus court de cette liste est : #{shortest_handle}"
  end
  
  def count_handles_with_5_characters(handles)
    handles_with_5_characters = handles.count { |handle| handle.length == 6 }
    puts "Il y a #{handles_with_5_characters} handles contenant 5 caractères (hors @)."
  end
  
  def count_capitalized_handles(handles)
    capitalized_handles = handles.count { |handle| handle[1].match?(/[A-Z]/) }
    puts "Il y a #{capitalized_handles} handles commençant par une majuscule."
  end
  
  def sort_handles_alphabetically(handles)
    sorted_handles_alphabetically = handles.sort
    puts "Liste de handles triée par ordre alphabétique :"
    puts sorted_handles_alphabetically
  end
  
  def sort_handles_by_length(handles)
    sorted_handles_by_length = handles.sort_by { |handle| handle.length }
    puts "Liste de handles triée par taille des handles :"
    puts sorted_handles_by_length
  end
  
  def find_epenser_index(handles)
    epenser_index = handles.index("@epenser")
    puts "La position de @epenser dans l'array est : #{epenser_index + 1}" if epenser_index
  end
  
  def handle_lengths_histogram(handles)
    handle_lengths = handles.map { |handle| handle.length - 1 }
    handle_lengths_histogram = handle_lengths.group_by(&:itself).transform_values(&:count)
    puts "Répartition des handles par taille :"
    handle_lengths_histogram.each { |length, count| puts "#{length} caractère(s) : #{count} handle(s)" }
  end
  
  def display_menu
    puts "Menu :"
    puts "1. Combien y a-t-il de handle dans cette array ?"
    puts "2. Quel est le handle le plus court de cette liste ?"
    puts "3. Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère) ?"
    puts "4. Combien commencent par une majuscule ?"
    puts "5. Trie la liste de handle par ordre alphabétique."
    puts "6. Trie la liste de handle par taille des handle."
    puts "7. Quelle est la position dans l'array de la personne @epenser ?"
    puts "8. Sors-moi une répartition des handle par taille."
    puts "9. Quitter"
  end
  
  def process_choice(choice, handles)
    case choice
    when 1
      count_handles(handles)
    when 2
      shortest_handle(handles)
    when 3
      count_handles_with_5_characters(handles)
    when 4
      count_capitalized_handles(handles)
    when 5
      sort_handles_alphabetically(handles)
    when 6
      sort_handles_by_length(handles)
    when 7
      find_epenser_index(handles)
    when 8
      handle_lengths_histogram(handles)
    when 9
      puts "Au revoir !"
      exit
    else
      puts "Choix invalide, veuillez entrer un numéro de 1 à 9."
    end
  end
  
  def run_program(handles)
    loop do
      display_menu
      print "Entrez le numéro de la question que vous souhaitez poser : "
      choice = gets.chomp.to_i
      process_choice(choice, handles)
      puts "\n"
    end
  end

